import React, { useState, useRef } from 'react'
import styles from './formContainer.module.css'
// Name
// Mobile
// Email
// Acc no
// Start date - current date
// Expiry date - duration
// Debit amount
// Debit Frequency
// IFSC Code
// PAN
// Account type
// Bank Code
const formContainer = (props) => {

    return (
        <>
            <label htmlFor = "Customer_Name"></label>
            <input type = "text" id = "Customer_Name"></input>

            <label htmlFor = "Customer_Mobile"></label>
            <input type = "number" id = "Customer_Mobile"></input>

            <label htmlFor = {props.fieldName}></label>
            <input type = {props.fieldType} id = {props.fieldName}></input>

            <label htmlFor = {props.fieldName}></label>
            <input type = {props.fieldType} id = {props.fieldName}></input>

            <label htmlFor = {props.fieldName}></label>
            <input type = {props.fieldType} id = {props.fieldName}></input>

            <label htmlFor = {props.fieldName}></label>
            <input type = {props.fieldType} id = {props.fieldName}></input>

            <label htmlFor = {props.fieldName}></label>
            <input type = {props.fieldType} id = {props.fieldName}></input>

            <label htmlFor = {props.fieldName}></label>
            <input type = {props.fieldType} id = {props.fieldName}></input>

            <label htmlFor = {props.fieldName}></label>
            <input type = {props.fieldType} id = {props.fieldName}></input>

            <label htmlFor = {props.fieldName}></label>
            <input type = {props.fieldType} id = {props.fieldName}></input>

            <label htmlFor = {props.fieldName}></label>
            <input type = {props.fieldType} id = {props.fieldName}></input>

            <label htmlFor = {props.fieldName}></label>
            <input type = {props.fieldType} id = {props.fieldName}></input>
        </>
    )
}

export default formContainer
