export const formatHour = (hourStr) => {
  if (typeof hourStr !== typeof '') throw Error('Bad arg')

  let hourFormat = ''

  let intHour = Number(hourStr)
  if (intHour > 12) {
    intHour -= 12
    hourFormat = 'PM'
  } else {
    hourFormat = 'AM'
  }

  const res = `${intHour.toString()}${hourFormat}`

  return res
}

