export const getExplicitTz = (tz) => {
  let explicitTz
  if (tz === 'utc') {
    explicitTz = 'UTC'
  } else if (tz === 'local') {
    explicitTz = Intl.DateTimeFormat().resolvedOptions().timeZone
  }
  return explicitTz
}

export const getCurrentTime = (timeZone) => {
  let now
  if (timeZone === 'UTC') {
    now = new Date(new Date().toUTCString().slice(0, -3))
  } else if (timeZone === 'local') {
    now = new Date()
  } else if (typeof timeZone === typeof '') {
    const date = new Date()
    now = new Date(
      date.toLocaleString('en-US', {
        timeZone: timeZone
      })
    )
  } else {
    throw Error(`Unsupported timezone: ${timeZone}`)
  }

  return now
}
import React, { createContext, useEffect } from 'react'
import PropTypes from 'prop-types'
import {
  validateValueTypes,
  validateConfigLength,
  isEmpty
} from '../../common/utils/errHandler'
import {
  validateMin,
  validateHour,
  validateDom,
  validateMon,
  validateDow,
  IsNeededToRunNow
} from '../../common/utils/validateTime'
import {
  converConfigValuesToObject,
  getCurrentTime
} from '../../common/utils/utils'

const foramtDow = (dow) => {
  if (dow === 0) {
    return 7
  } else {
    return dow
  }
}

const timerDuration = 60000

const detectTaskTime = (convertedConfigArr, timeZone) => {
  // console.log('timeZone ::', timeZone)
  const now = getCurrentTime(timeZone)

  const currentMin = now.getMinutes()
  const currentHour = now.getHours()
  const currentDom = now.getDate()
  const Mon = now.getMonth() // beware: January = 0; February = 1, etc.
  const Dow = now.getDay()
  const currentMon = Mon + 1
  const currentDow = foramtDow(Dow) // Sunday = 0, Monday = 1, etc.
  // console.log('now ::', now)
  // console.log('now currentMin::', currentMin)
  // console.log('now currentHour::', currentHour)
  // console.log('now currentDom::', currentDom)
  // console.log('now currentMon::', currentMon)
  // console.log('now currentDow::', currentDow)

  const min = convertedConfigArr[0]
  const hour = convertedConfigArr[1]
  const dom = convertedConfigArr[2]
  const mon = convertedConfigArr[3]
  const dow = convertedConfigArr[4]

  const minValidateRes = IsNeededToRunNow(min, currentMin.toString())
  const hourValidateRes = IsNeededToRunNow(hour, currentHour.toString())
  const domValidateRes = IsNeededToRunNow(dom, currentDom.toString())
  const monValidateRes = IsNeededToRunNow(mon, currentMon.toString())
  const dowValidateRes = IsNeededToRunNow(dow, currentDow.toString())

  if (!minValidateRes.isNeededToRun) {
    return minValidateRes.isNeededToRun
  }
  if (!hourValidateRes.isNeededToRun) {
    return hourValidateRes.isNeededToRun
  }
  if (!domValidateRes.isNeededToRun) {
    return domValidateRes.isNeededToRun
  }
  if (!monValidateRes.isNeededToRun) {
    return monValidateRes.isNeededToRun
  }
  if (!dowValidateRes.isNeededToRun) {
    return dowValidateRes.isNeededToRun
  }

  // console.log('minValidateRes ::', minValidateRes.isNeededToRun)

  // console.log('hourValidateRes ::', hourValidateRes.isNeededToRun)
  // console.log('domValidateRes ::', domValidateRes.isNeededToRun)

  // console.log('monValidateRes ::', monValidateRes.isNeededToRun)

  // console.log('dowValidateRes ::', dowValidateRes.isNeededToRun)

  return true
}

const handleSetTimer = (job, timeZone) => {
  const { schedule } = job
  const splittedConfig = schedule.split(' ')
  const res = validateConfigLength(splittedConfig)
  const resTwo = isEmpty(splittedConfig)

  if (res.error) {
    throw Error(res.msg)
  }
  if (resTwo.error) {
    throw Error(resTwo.msg)
  }

  // console.log('schedule :::', schedule)
  // const utcTime = new Date(new Date().toUTCString().slice(0, -3))
  // console.log('utc::', utcTime)

  const isNotAsterisk = splittedConfig
    .slice(0, splittedConfig.length - 1)
    .find((item) => item !== '*')
  if (isNotAsterisk === undefined) {
    // means this is all *
    // console.log('isNotAsterisk ::', isNotAsterisk)
    const { fn } = job

    fn()
    setInterval(() => {
      fn()
    }, timerDuration)
  } else {
    // means there is something particular
    // validate value in schedule field
    const convertedConfig = splittedConfig.map((item) => {
      const obj = converConfigValuesToObject(item)

      return obj
    })

    // console.log('convertedConfig ::', convertedConfig)

    const minValidateRes = validateMin(convertedConfig[0])

    const hourValidateRes = validateHour(convertedConfig[1])

    const domValidateRes = validateDom(convertedConfig[2])

    const monValidateRes = validateMon(convertedConfig[3])

    const dowValidateRes = validateDow(convertedConfig[4])

    if (minValidateRes.error) {
      const { msg } = minValidateRes
      throw Error(msg)
    } else if (hourValidateRes.error) {
      const { msg } = hourValidateRes
      throw Error(msg)
    } else if (domValidateRes.error) {
      const { msg } = domValidateRes
      throw Error(msg)
    } else if (monValidateRes.error) {
      const { msg } = monValidateRes
      throw Error(msg)
    } else if (dowValidateRes.error) {
      const { msg } = dowValidateRes
      throw Error(msg)
    }

    const { fn } = job

    const isNeededToRun = detectTaskTime(convertedConfig, timeZone)
    if (isNeededToRun) {
      // console.log('isNeededToRun ::', isNeededToRun)
      fn()
    }

    setInterval(() => {
      const isNeededToRun = detectTaskTime(convertedConfig, timeZone)
      if (isNeededToRun) {
        fn()
      }
    }, timerDuration)
  }
}
const BasicCronContext = createContext(null)

const BasicCronProvider = ({ children, jobs, timeZone }) => {


  useEffect(() => {
    if (jobs.length) {
      const validatedTasks = validateValueTypes(jobs)

      for (const item of validatedTasks) {
        handleSetTimer(item, timeZone)
      }
    }
  }, [])

  const store = {
    jobs,
    timeZone
  }
  return (
    <BasicCronContext.Provider value={store}>
      {children}
    </BasicCronContext.Provider>
  )
}

BasicCronProvider.propTypes = {
  jobs: PropTypes.array.isRequired,
  timeZone: PropTypes.string.isRequired
}

export { BasicCronProvider, BasicCronContext }
import React, { useContext } from 'react'
import PropTypes from 'prop-types'
import { BasicCronContext } from '../../../contexts/basic/BasicCronContext'
import styles from '../../../styles.module.css'
import {
  formatDOW,
  formatHour,
  formatMonth,
  converConfigValuesToObject,
  getCurrentTime,
  formatMonthInDashboard
} from '../../../common/utils/utils'
import { ASTERISK } from '../../../common/data/types'

const addHrTime = (jobs) => {
  if (!jobs.length) return []
  const result = jobs.map((job) => {
    // let year = "2020";
    const { schedule } = job
    const splittedConfig = schedule.split(' ')
    const convertedConfig = splittedConfig.map((item) => {
      const obj = converConfigValuesToObject(item)
      return obj
    })
    let hrTime = ''

    // const { hour, hourFormat } = formatHour(convertedConfig[1])
    // const inserted = insertZero(splitted)
    // // console.log('inserted ::', inserted)
    const min = convertedConfig[0]
    const hour = convertedConfig[1]
    const dom = convertedConfig[2]
    const mon = formatMonth(convertedConfig[3])

    const dow = formatDOW(convertedConfig[4])

    if (mon.type === ASTERISK) {
      hrTime += 'Every Month'
    } else {
      const arr = mon.value
      hrTime += `In`
      for (let i = 0; i < arr.length; i++) {
        if (i === 0) {
          hrTime += ` ${arr[i]}`
        } else {
          hrTime += `, ${arr[i]}`
        }
      }
    }

    if (dom.type === ASTERISK) {
      hrTime += ''
    } else {
      const arr = dom.value
      hrTime += ` on`
      for (let i = 0; i < arr.length; i++) {
        if (i === 0) {
          hrTime += ` ${arr[i]}th`
        } else {
          hrTime += `, ${arr[i]}th`
        }
      }
    }

    if (dow.type === ASTERISK) {
      hrTime += ''
    } else {
      const arr = dow.value
      if (dom.type === ASTERISK) {
        hrTime += ` on`
      } else {
        hrTime += ` and`
      }
      for (let i = 0; i < arr.length; i++) {
        if (i === 0) {
          hrTime += ` ${arr[i]}`
        } else {
          hrTime += `, ${arr[i]}`
        }
      }
    }

    if (hour.type === ASTERISK) {
      hrTime += ' every hour'
    } else {
      const arr = hour.value
      hrTime += ` At`
      for (let i = 0; i < arr.length; i++) {
        const res = formatHour(arr[i])
        if (i === 0) {
          hrTime += ` ${res}`
        } else {
          hrTime += `, ${res}`
        }
      }
    }

    if (min.type === ASTERISK) {
      hrTime += ' every minute'
    } else {
      const arr = min.value
      if (hour.type !== ASTERISK) {
        hrTime += ` and At`
      } else {
        hrTime += ` At`
      }
      for (let i = 0; i < arr.length; i++) {
        if (i === 0) {
          hrTime += ` ${arr[i]} minute(s)`
        } else {
          hrTime += `, ${arr[i]} minute(s)`
        }
      }
    }

    // // since we have 32 possibilities, we are gonna have 32 if statements
    // const conditions = { min, hour, dom, mon, dow }
    // const res = getHRtime(hrTime, conditions)

    return { ...job, hrTime }
  })

  return result
}

const handleFormatTz = (tz) => {
  if (tz === 'local') {
    return 'LOCAL'
  }
  return tz
}

const Dashboard = (props) => {
  const { jobs, timeZone } = useContext(BasicCronContext)
  // console.log('jobs in Dashboard', jobs)
  const now = getCurrentTime(timeZone)

  const currentDom = now.getDate()
  const Mon = now.getMonth() // beware: January = 0; February = 1, etc.
  const currentMon = Mon + 1
  const formattedMonth = formatMonthInDashboard(currentMon.toString())
  const crons = addHrTime(jobs)
  const tzText = handleFormatTz(timeZone)
  // console.log('crons :::')
  return (
    <div className={styles.dashboard}>
      <table className={styles.dashboard__container}>
        <thead className={styles.dashboard__title__container}>
          <tr>
            <th colSpan='5' className={styles.dashboard__title}>
              <div className={styles.dashboard__title__box}>
                <span>Dashboard</span>
                <span>
                  {`Today: ${formattedMonth} ${currentDom}th / TZ: ${tzText}`}
                </span>
              </div>
            </th>
          </tr>
        </thead>
        <thead>
          <tr>
            <th>ID</th>
            <th>Name</th>
            <th>Schedule</th>
            <th>Schedule (Human Readable)</th>
          </tr>
        </thead>
        {crons.length &&
          crons.map((cron, index) => (
            <tbody key={index}>
              <tr>
                <td>{cron.id}</td>
                <td>{cron.name}</td>
                <td>{cron.schedule}</td>
                <td>{`${cron.hrTime} `}</td>
              </tr>
            </tbody>
          ))}
      </table>
    </div>
  )
}

Dashboard.propTypes = {}

export default Dashboard
