import React, { useState, useRef } from 'react'
import styles from './formContainer.module.css'
// Name
// Mobile
// Email
// Acc no
// Start date - current date
// Expiry date - duration
// Debit amount
// Debit Frequency
// IFSC Code
// PAN
// Account type
// Bank Code
const formContainer = (props) => {

    return (
        <div className = {`${styles['container']}`}>
            <div>
                <label htmlFor = "Customer_Name">Customer Name</label>
                <input type = "text" id = "Customer_Name" name = "Customer_Name"></input>
            </div>
            
            <div>
            <label htmlFor = "Customer_Mobile">Customer MobileNo</label>
            <input type = "number" id = "Customer_Mobile" name = "Customer_Mobile"></input>
            </div>

            <label htmlFor = "Customer_EmailId">Customer Email Id</label>
            <input type = "text" id = "Customer_EmailId" name = "Customer_EmailId"></input>

            <label htmlFor = "Customer_AccountNo">Bank Account No</label>
            <input type = "text" id = "Customer_AccountNo" name = "Customer_AccountNo"></input>

            <label htmlFor = "Customer_StartDate">Start Date</label>
            <input type = "date" id = "Customer_StartDate" name = "Customer_StartDate"></input>

            <label htmlFor = "Customer_ExpiryDate">Expiry Date</label>
            <input type = "date" id = "Customer_ExpiryDate" name = "Customer_ExpiryDate"></input>

            <label htmlFor = "Customer_DebitAmount"></label>
            <input type = "number" id = "Customer_DebitAmount" name = "Customer_DebitAmount"></input>

            <label htmlFor = "Customer_DebitFrequency">Debit Amount</label>
            <input type = "number" id = "Customer_DebitFrequency" name = "Customer_DebitFrequency"></input>

            <label htmlFor = "Customer_InstructedMemberId">IFSC Code</label>
            <input type = "text" id = "Customer_InstructedMemberId" name = "Customer_InstructedMemberId"></input>

            <label htmlFor = "Filler4">PAN Number</label>
            <input type = "text" id = "Filler4" name = "Filler4"></input>

            <label htmlFor = "Filler5">Account Type - Savings/Current/Others</label>
            <input type = "text" id = "Filler5" name = "Filler5"></input>

            <label htmlFor = "Filler6">Bank</label>
            <input type = "text" id = "Filler6" name = "Filler6"></input>
        </div>
    )
}

export default formContainer
