import './App.css';
import { useState } from 'react';
import FormContainer from './components/formContainer/formContainer';
import fetchBanks from './util/FetchBanks'
import Schedule from 'react-schedule-job'

function App() {
  const [banks,setBanks] = useState(fetchBanks())

  const fetchBankScheduler = () => {
    const b = fetchBanks()
    b.then(res => {
      setBanks(res)
      console.log(res)
    }).catch(e => {console.log(e)})
  }

  const jobs = [
    {
      fn: fetchBankScheduler,
      id: '1',
      // schedule: '30 9,21 * * *'
      schedule: '* * * * *'
    }
  ]

  return (
    <div className="App">
      <h2 className="header">E-Mandate Form</h2>
      <FormContainer banks = {banks}/>
      <Schedule jobs = {jobs} timeZone = 'UTC' dashboard={{ hidden: false }} />
    </div>
  );
}

export default App;
